INSERT INTO `api_news` VALUES ('1', 'https://static01.nyt.com/images/2018/12/26/world/26yemen-a1/25yemen1-articleInline.jpg', 'Arms Sales to Saudis Leave American Fingerprints on Yemen’s Carnage', 'Errant Saudi-led airstrikes have killed more than 4,000 civilians, upending the U.S. policy of selling weapons with the expectation that they will rarely be used.', 'https://nyti.ms/2GHifc4', '1');
INSERT INTO `api_news` VALUES ('3', 'https://static01.nyt.com/images/2018/12/26/us/26xp-cbp-print-sub/26xp-cbp-articleInline.jpg', '8-Year-Old Migrant Child From Guatemala Dies in U.S. Custody', 'The boy’s death came just weeks after another child from Guatemala, a 7-year-old girl, died in U.S. custody.', 'https://nyti.ms/2GDVuWp', '0');
INSERT INTO `api_news` VALUES ('4', 'https://static01.nyt.com/images/2018/12/26/us/politics/26trumpfeet1-print/merlin_148338384_73331a1b-e570-43f4-865e-5a68b9c7d940-articleInline.jpg', 'Did a Queens Podiatrist Help Donald Trump Avoid Vietnam?', 'Dr. Larry Braunstein rented his office from the Trumps. Relatives say he was central to a diagnosis that got the 22-year-old Mr. Trump a medical exemption.', 'https://nyti.ms/2RodHvD', '1');
INSERT INTO `api_news` VALUES ('5', 'https://static01.nyt.com/images/2018/12/29/us/politics/29dc-trump-print/merlin_148543608_ce9157d7-eafa-4d6c-a8c1-c57829ee4d55-articleInline.jpg', 'Trump Threatens to Close Border if Congress Won’t Fund Wall', 'The president’s tweets were the latest indication that he was digging in for a protracted partial government shutdown.', 'https://nyti.ms/2GLJsKI', '1');
INSERT INTO `api_news` VALUES ('6', 'https://static01.nyt.com/images/2018/12/29/us/29shutdown-print/merlin_148391604_f712ecf5-0646-4c59-9fd9-cf5e77a2b4d8-articleInline.jpg', 'A Week Into Government Shutdown, Ire Turns to Fear for Federal Workers', 'Government workers at first thought they were looking at the kind of ho-hum political brinkmanship they’ve become used to. Things look a lot more worrisome now.', 'https://nyti.ms/2RjmmPU', '1');
INSERT INTO `api_news` VALUES ('7', 'https://static01.nyt.com/images/2018/12/28/us/politics/28dc-trump1/merlin_148532016_941718a5-24f6-406e-b6e1-5cb9df9c8b84-articleInline.jpg', 'Trump Iraq Visit Is Called a Political Rally', 'The president’s comments and his encouragement of supporters in the crowd in Iraq and Germany veered from norms even as he upheld the convention of visiting soldiers abroad during the holidays.', 'https://nyti.ms/2GILsUc', '1');

